int32 err;

err = OS_TaskCreate(NULL,                   /* Error.*/
                    "failing thread",
                    test_thread1,
                    (uint32 *)wa_test1,
                    sizeof wa_test1,
                    TASKS_BASE_PRIORITY,
                    0);
test_assert(err == OS_INVALID_POINTER, "NULL not detected");
test_assert_sequence("", "task executed");

int32 err;
uint32 tid;

